CREATE OR REPLACE FUNCTION pls_obter_se_internado_req ( nr_seq_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE

		
ie_retorno_w			varchar(1)	:= 'N';
ie_tipo_guia_w			pls_requisicao.ie_tipo_guia%type;
ie_internado_w			varchar(1)	:= 'N';
ie_tipo_atendimento_w		varchar(2);
cd_tiss_w			varchar(20);	
ie_regime_atendimento_w	pls_requisicao.ie_regime_atendimento%type;


BEGIN

select	coalesce(ie_tipo_guia,'0'),
	coalesce(ie_tipo_atendimento,'0'),
	coalesce(ie_regime_atendimento, '0')
into STRICT	ie_tipo_guia_w,
	ie_tipo_atendimento_w,
	ie_regime_atendimento_w
from	pls_requisicao
where	nr_sequencia	= nr_seq_requisicao_p;

if (ie_tipo_guia_w in ('1','8')) or
	(ie_tipo_guia_w = '2' AND (ie_tipo_atendimento_w = '7' or ie_regime_atendimento_w = '03')) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
