CREATE OR REPLACE FUNCTION pls_obter_se_item_composicao ( nr_seq_pacote_p pls_pacote.nr_sequencia%type, nr_seq_material_p pls_conta_mat.nr_seq_material%type, cd_procedimento_p pls_conta_proc.cd_procedimento%type, ie_origem_proced_p pls_conta_proc.ie_origem_proced%type) RETURNS varchar AS $body$
DECLARE


/*Esta função tem como objetivo retornar se o item(proc/mat) faz parte
da composição de determinado pacote e se esse item tem valor negociado
pois se no mesmo não houver valor negociado deverá calcular o valor
para os itens do pacote pela regra de preço.*/
ie_composicao_w	varchar(1)	:= 'S';

BEGIN

if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '')	then

	begin
	select 	CASE WHEN count(a.nr_sequencia)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_composicao_w
	from	pls_pacote_composicao	a,
		pls_pacote_procedimento	b
	where	b.nr_seq_composicao	= a.nr_sequencia
	and	b.cd_procedimento	= cd_procedimento_p
	and	b.ie_origem_proced	= ie_origem_proced_p
	and	a.nr_seq_pacote		= nr_seq_pacote_p
	and	((b.vl_negociado	= 0) or (coalesce(b.vl_negociado::text, '') = ''));
	exception
	when others then
		ie_composicao_w		:= 'N';
	end;

	if (ie_composicao_w = 'N')	then
		select	CASE WHEN count(nr_sequencia)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_composicao_w
		from	pls_pacote
		where	cd_procedimento 	= cd_procedimento_p
		and	ie_origem_proced	= ie_origem_proced_p
		and	nr_sequencia		= nr_seq_pacote_p;
	end if;

elsif (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '')	then

	begin
	select 	CASE WHEN count(a.nr_sequencia)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_composicao_w
	from	pls_pacote_composicao	a,
		pls_pacote_material	b
	where	b.nr_seq_composicao	= a.nr_sequencia
	and	b.nr_seq_material	= nr_seq_material_p
	and	a.nr_seq_pacote		= nr_seq_pacote_p
	and	((b.vl_negociado	= 0) or (coalesce(b.vl_negociado::text, '') = ''));
	exception
	when others then
		ie_composicao_w		:= 'N';
	end;
end if;

return	ie_composicao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
