SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_item_dentro_int ( nr_seq_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ie_internado_w			varchar(1);
dt_entrada_w			timestamp;
dt_alta_w			timestamp;
dt_proc_max_w			timestamp;
dt_proc_min_w			timestamp;
hr_inicio_w			timestamp;
hr_fim_w			timestamp;
nr_seq_segurado_w		bigint;
qt_reg_w			integer;
ds_retorno_w			varchar(1) := 'N';


BEGIN

/*Obter se a conta que esta sendo consistida é de internação*/

ie_internado_w := pls_obter_se_internado(nr_seq_conta_p,'');

select	dt_entrada,
	dt_alta,
	nr_seq_segurado
into STRICT	dt_entrada_w,
	dt_alta_w,
	nr_seq_segurado_w
from	pls_conta
where	nr_sequencia = nr_seq_conta_p;

if (ie_internado_w = 'S') then
	/* Se for uma conta de internação, então irá verificar se existem procedimentos , em outras contas , dentro do periodo de internação e
	o mesmo beneficiario da conta consistida e que NÃO SEJAM de internação. */
	/*
	select	count(*)
	into	qt_reg_w
	from	pls_conta_proc		b,
		pls_conta		a
	where	a.nr_sequencia		= b.nr_seq_conta
	and	a.nr_sequencia		<> nr_seq_conta_p
	and	a.nr_seq_segurado 	= nr_seq_segurado_w
	and	b.dt_procedimento between dt_entrada_w and dt_alta_w
	and	pls_obter_se_internado(a.nr_sequencia,'') = 'N';*/
	select	count(*)
	into STRICT	qt_reg_w
	from	pls_conta_proc		b,
		pls_conta		a
	where	a.nr_sequencia		= b.nr_seq_conta
	and	a.nr_sequencia		<> nr_seq_conta_p
	and	a.nr_seq_segurado 	= nr_seq_segurado_w
	and	(((b.dt_inicio_proc IS NOT NULL AND b.dt_inicio_proc::text <> '') and (b.dt_procedimento IS NOT NULL AND b.dt_procedimento::text <> '') and (to_date(to_char(b.dt_procedimento,'dd/mm/yyyy')||' '||to_char(b.dt_inicio_proc,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
		between dt_entrada_w and dt_alta_w))
	or	((b.dt_fim_proc IS NOT NULL AND b.dt_fim_proc::text <> '') and (b.dt_procedimento IS NOT NULL AND b.dt_procedimento::text <> '') and (to_date(to_char(b.dt_procedimento,'dd/mm/yyyy')||' '||to_char(b.dt_fim_proc,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
		between dt_entrada_w and dt_alta_w))
	or (trunc(b.dt_procedimento) between trunc(dt_entrada_w) and trunc(dt_alta_w) and (coalesce(b.dt_inicio_proc::text, '') = '' and coalesce(b.dt_fim_proc::text, '') = '')))
	and	pls_obter_se_internado(a.nr_sequencia,'') = 'N';

	/* Se o qt_reg_w for maior que 0, então exitem procedimentos dentro do periodo de internação da conta para o mesmo beneficiario  */

	if (qt_reg_w > 0) then
		ds_retorno_w := 'S';
	end if;
else
	/* Se não for uma conta de internação, então irá verificar, com as datas dos procedimentos (dt_proc_max_w 'maoir data', dt_proc_min_w 'menor data')  da conta que
	está sendo consistida , se exitem contas de internação em que estas datas dos procedimentos estejam dentro do periodo de entrada e saida das contas de internação.
	Tambem verificando somente se for o mesmo beneficiario.*/
	select	max(dt_procedimento),
		min(dt_procedimento)
	into STRICT	dt_proc_max_w,
		dt_proc_min_w
	from	pls_conta_proc
	where	nr_seq_conta = nr_seq_conta_p;

	select	count(*)
	into STRICT	qt_reg_w
	from	pls_conta_proc		b,
		pls_conta		a
	where	a.nr_sequencia 		= b.nr_seq_conta
	and	a.nr_sequencia 		<> nr_seq_conta_p
	and	a.nr_seq_segurado 	= nr_seq_segurado_w
	and	obter_se_datas_entre_periodo(a.dt_entrada,a.dt_alta,dt_proc_min_w,dt_proc_max_w) = 'S'
	and	pls_obter_se_internado(a.nr_sequencia,'') = 'S';

	/* Se o qt_reg_w for maior que 0, então exitem contas de internação em que o periodo de internação da conta é a mesma que as datas dos procedimentos da conta consistida */

	if (qt_reg_w > 0) then
		ds_retorno_w := 'S';
	end if;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
