SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_item_esquema_lib ( nr_seq_esquema_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w		bigint;
IE_TIPO_REGRA_w		varchar(10);
nr_seq_item_cont_w	bigint;


BEGIN

select	IE_TIPO_REGRA
into STRICT	IE_TIPO_REGRA_w
from	PLS_ESQUEMA_CONTABIL
where	nr_sequencia	= nr_seq_esquema_p;

select	max(nr_sequencia)
into STRICT	nr_seq_item_cont_w
from	PLS_ITEM_CRITERIOS_CONTAB
where	IE_TIPO_REGRA	= IE_TIPO_REGRA_w;

select	count(1)
into STRICT	qt_registro_w
from	PLS_ITEM_CRIT_CONT_PERFIL
where	NR_SEQ_ITEM_CONTAB	= nr_seq_item_cont_w
and	cd_perfil		= cd_perfil_p;

if (qt_registro_w = 0) then
	return 'N';
else
	return 'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
