SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_item_exec_analise ( nr_seq_exec_item_w bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1)	:= 'N';
nr_seq_req_proc_w		bigint;
nr_seq_req_mat_w		bigint;
nr_seq_execucao_w		bigint;
nr_seq_requisicao_w		bigint;
qt_reg_w			integer;


BEGIN

select	nr_seq_req_proc,
	nr_seq_req_mat,
	nr_seq_execucao,
	nr_seq_requisicao
into STRICT	nr_seq_req_proc_w,
	nr_seq_req_mat_w,
	nr_seq_execucao_w,
	nr_seq_requisicao_w
from	pls_execucao_req_item
where	nr_sequencia = nr_seq_exec_item_w;

if (coalesce(nr_seq_req_proc_w,0) > 0) then
	select	count(*)
	into STRICT	qt_reg_w
	from	pls_auditoria		b,
		pls_auditoria_item	a
	where	b.nr_sequencia		= a.nr_seq_auditoria
	and	coalesce(b.dt_liberacao::text, '') = ''
	and	a.nr_seq_proc_origem	in (	SELECT	nr_sequencia
						from	pls_execucao_req_item
						where	nr_seq_req_proc	= nr_seq_req_proc_w
						and	nr_seq_requisicao = nr_seq_requisicao_w
						and	nr_seq_execucao <> nr_seq_execucao_w);

elsif (coalesce(nr_seq_req_mat_w,0) > 0) then
	select	count(*)
	into STRICT	qt_reg_w
	from	pls_auditoria		b,
		pls_auditoria_item	a
	where	b.nr_sequencia		= a.nr_seq_auditoria
	and	coalesce(b.dt_liberacao::text, '') = ''
	and	a.nr_seq_mat_origem	in (	SELECT	nr_sequencia
						from	pls_execucao_req_item
						where	nr_seq_req_mat	= nr_seq_req_mat_w
						and	nr_seq_requisicao = nr_seq_requisicao_w
						and	nr_seq_execucao <> nr_seq_execucao_w);
end if;


if (qt_reg_w > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
