CREATE OR REPLACE FUNCTION pls_obter_se_item_glosa_ocorr ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, nr_seq_item_p bigint, ie_tipo_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
qt_ocorrencia_w			bigint  := 0;


BEGIN

if (ie_tipo_item_p = 1) then
	select	count(1)
	into STRICT	qt_ocorrencia_w
	from	pls_ocorrencia_benef	b,
		pls_ocorrencia		a
	where	b.nr_seq_guia_plano = nr_seq_guia_p
	and	b.nr_seq_ocorrencia = a.nr_sequencia
	and	a.ie_auditoria = 'S'
	and	nr_seq_proc	= nr_seq_item_p;

elsif (ie_tipo_item_p = 2) then
	select	count(1)
	into STRICT	qt_ocorrencia_w
	from	pls_ocorrencia_benef	b,
		pls_ocorrencia		a
	where	b.nr_seq_guia_plano = nr_seq_guia_p
	and	b.nr_seq_ocorrencia = a.nr_sequencia
	and	a.ie_auditoria = 'S'
	and	nr_seq_mat	= nr_seq_item_p;
elsif (ie_tipo_item_p = 5) then
	select	count(1)
	into STRICT	qt_ocorrencia_w
	from	pls_ocorrencia_benef	b,
		pls_ocorrencia		a
	where	b.nr_seq_requisicao = nr_seq_requisicao_p
	and	b.nr_seq_ocorrencia = a.nr_sequencia
	and	a.ie_auditoria = 'S'
	and	nr_seq_proc	= nr_seq_item_p;

elsif (ie_tipo_item_p = 6) then
	select	count(1)
	into STRICT	qt_ocorrencia_w
	from	pls_ocorrencia_benef	b,
		pls_ocorrencia		a
	where	b.nr_seq_requisicao = nr_seq_requisicao_p
	and	b.nr_seq_ocorrencia = a.nr_sequencia
	and	a.ie_auditoria = 'S'
	and	nr_seq_mat	= nr_seq_item_p;
end if;

if (qt_ocorrencia_w > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
