SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_item_lista ( ie_tipo_item_p text, ie_item_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(4000) := 'N';
ds_lista_w			varchar(4000);
pos_virgula_w			bigint;
ie_acao_w			varchar(4000);
ds_item_w			varchar(4000);
pos_virgula_ww			bigint;
ie_tipo_item_w			varchar(4000);


BEGIN

ds_lista_w	:= ie_tipo_item_p || ',';

if (coalesce(ie_tipo_item_p,'0') <> '0') then
	while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop
		begin
		ds_item_w	:= ie_item_p || ',';

		select  position(',' in ds_lista_w)
		into STRICT    pos_virgula_w
		;

		if (pos_virgula_w > 0) then
			ie_acao_w	:= substr(ds_lista_w,1,pos_virgula_w-1);
			while(ds_item_w IS NOT NULL AND ds_item_w::text <> '') and (ds_retorno_w = 'N') loop
				begin
				select  position(',' in ds_item_w)
				into STRICT    pos_virgula_ww
				;

				if (pos_virgula_ww > 0) then
					ie_tipo_item_w	:= substr(ds_item_w,1,pos_virgula_ww-1);

					if (ie_tipo_item_w = ie_acao_w and ds_retorno_w = 'N') then
						ds_retorno_w := 'S';
					end if;
				end if;
				ds_item_w	:= substr(ds_item_w,pos_virgula_ww+1,length(ds_item_w));
				end;
			end loop;
			if (ie_acao_w IS NOT NULL AND ie_acao_w::text <> '') then
				ds_lista_w	:= substr(ds_lista_w,pos_virgula_w+1,length(ds_lista_w));
			else
				ds_lista_w	:= null;
			end if;
		end if;
		end;
	end loop;
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
