SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_item_pend_exec ( nr_seq_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se existe algum item da requisição pendente para execução
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_proc_pendente_w		pls_requisicao_proc.qt_proc_executado%type	:= 0;
qt_mat_pendente_w		pls_requisicao_mat.qt_mat_executado%type	:= 0;
ds_retorno_w			varchar(1)	:= 'N';

BEGIN

if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	begin
		select	count(1)
		into STRICT	qt_proc_pendente_w
		from	pls_requisicao_proc	a
		where	a.nr_seq_requisicao	= nr_seq_requisicao_p
		and	a.qt_procedimento	> coalesce(a.qt_proc_executado, 0);
	exception
	when others then
		qt_proc_pendente_w := 0;
	end;

	begin
		select	count(1)
		into STRICT	qt_mat_pendente_w
		from	pls_requisicao_mat	a
		where	a.nr_seq_requisicao	= nr_seq_requisicao_p
		and	a.qt_material		> coalesce(a.qt_mat_executado, 0);
	exception
	when others then
		qt_mat_pendente_w := 0;
	end;

	if (qt_proc_pendente_w > 0) or (qt_mat_pendente_w > 0)then
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
