CREATE OR REPLACE FUNCTION pls_obter_se_item_regra_ptu ( cd_procedimento_p text, ie_origem_proced_p text, nr_seq_material_p bigint ) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se o procedimento/material informados existe nas regras cadastradas na função
OPS - Cadastro de Regras / OPS - Intercâmbio / PTU / Regra itens SCS / Códigos genéricos PTU
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_item_regra_w		bigint;
ds_retorno_w		varchar(1);


BEGIN

if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
	select	count(1)
	into STRICT	qt_item_regra_w
	from	pls_regra_codigo_gener_ptu
	where	cd_procedimento 	= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p
	and	ie_situacao		= 'A';

elsif (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
	select	count(1)
	into STRICT	qt_item_regra_w
	from	pls_regra_codigo_gener_ptu
	where	nr_seq_material 	= nr_seq_material_p
	and	ie_situacao		= 'A';

end if;

if (qt_item_regra_w > 0) then
	ds_retorno_w	:= 'S';
else
	ds_retorno_w	:= 'N';
end if;


return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
