CREATE OR REPLACE FUNCTION pls_obter_se_itens_aud_aprov ( nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
nr_seq_auditoria_w		bigint;
qt_itens_aud_w			integer;
qt_itens_aprovados_w		integer;


BEGIN

begin
	select	nr_sequencia
	into STRICT	nr_seq_auditoria_w
	from	pls_auditoria
	where	nr_seq_guia = nr_seq_guia_p;
exception
when others then
	nr_seq_auditoria_w := 0;
end;

if (nr_seq_auditoria_w > 0) then

	select	count(*)
	into STRICT	qt_itens_aud_w
	from	pls_auditoria_item
	where	nr_seq_auditoria = nr_seq_auditoria_w;

	select	count(*)
	into STRICT	qt_itens_aprovados_w
	from	pls_auditoria_item
	where	nr_seq_auditoria = nr_seq_auditoria_w
	and	ie_status = 'A';

	if (qt_itens_aud_w = qt_itens_aprovados_w) then
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
