CREATE OR REPLACE FUNCTION pls_obter_se_itens_aud_pos ( nr_seq_analise_p bigint, nr_seq_grupo_atual_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


/*
Obter se todas entre todas as ocorrências da análise existem ocorrência pendentes.
	S - Existe
	S - Não existe
*/
ds_retorno_w			varchar(1) := 'N';

C01 CURSOR FOR
	SELECT	'S'
	from	pls_analise_conta_item a
	where	a.nr_seq_analise = nr_seq_analise_p
	and	a.ie_status = 'P'
	and	a.ie_tipo = 'O'
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	ds_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
