SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_itens_cor_grupo ( nr_seq_analise_p bigint, nr_seq_grupo_atual_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'S';
nr_seq_analise_conta_item_w	bigint;	
	
/*Verificar se todos os itens que necessitam correcao do grupo foram corrigidos*/

	

BEGIN

select	CASE WHEN coalesce(max(x.nr_sequencia),0)=0 THEN  'S'  ELSE 'N' END
into STRICT	ds_retorno_w
from	pls_analise_conta_item x
where	x.nr_seq_analise = nr_seq_analise_p
and	x.ie_status	= 'E'
and	x.ie_tipo	= 'O'
and	exists	(	SELECT	a.nr_sequencia
			from	pls_membro_grupo_aud a,
				pls_ocorrencia_grupo b
			where	a.nr_seq_grupo			= b.nr_seq_grupo
			and	coalesce(a.nm_usuario_exec,'X') 	= nm_usuario_p
			and	a.nr_seq_grupo		 	= nr_seq_grupo_atual_p 
			and 	b.nr_seq_ocorrencia 		= (	select	max(y.nr_sequencia)
									from	pls_ocorrencia y
									where	y.cd_ocorrencia = x.cd_codigo	)
			and	a.ie_situacao = 'A'	
			and	b.ie_situacao = 'A')
order by 1;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
