SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_itens_guia_status ( nr_seq_guia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao

 'A' - se existem itens em análise
 'L' - se existem itens liberada
 'N' - se existem itens negados
'AT'- se todos os itens estam em analise
'AL' - se todos os itens estam aprovados
'AN' - se todos os itens estam negados
*/
qt_itens_w			integer;
qt_itens_aprovado_w		integer;
qt_itens_negados_w		integer;
qt_itens_analise_w		integer;
ds_retorno_w			varchar(1) := 'N';


BEGIN

/* obter quantidade de itens não excluidos*/

select	sum(qt_itens)
into STRICT	qt_itens_w
from (SELECT	count(1) qt_itens
	from	pls_guia_plano_proc
	where	nr_seq_guia = nr_seq_guia_p
	and	coalesce(nr_seq_motivo_exc::text, '') = ''
	
union

	SELECT	count(1) qt_itens
	from	pls_guia_plano_mat
	where	nr_seq_guia = nr_seq_guia_p
	and	coalesce(nr_seq_motivo_exc::text, '') = '') alias5;

/* obter quantidade de itens aprovados*/

select	sum(qt_itens)
into STRICT	qt_itens_aprovado_w
from (SELECT	count(1) qt_itens
	from	pls_guia_plano_proc
	where	nr_seq_guia = nr_seq_guia_p
	and	coalesce(nr_seq_motivo_exc::text, '') = ''
	and	ie_status in ('L','P','S')
	
union

	SELECT	count(1) qt_itens
	from	pls_guia_plano_mat
	where	nr_seq_guia = nr_seq_guia_p
	and	coalesce(nr_seq_motivo_exc::text, '') = ''
	and	ie_status in ('L','P','S')) alias7;

/* obter quantidade de itens negados*/

select	sum(qt_itens)
into STRICT	qt_itens_negados_w
from (SELECT	count(1) qt_itens
	from	pls_guia_plano_proc
	where	nr_seq_guia = nr_seq_guia_p
	and	coalesce(nr_seq_motivo_exc::text, '') = ''
	and	ie_status in ('K','M','N','C')
	
union

	SELECT	count(1) qt_itens
	from	pls_guia_plano_mat
	where	nr_seq_guia = nr_seq_guia_p
	and	coalesce(nr_seq_motivo_exc::text, '') = ''
	and	ie_status in ('K','M','N','C')) alias7;

/* obter quantidade de itens em analise*/

select	sum(qt_itens)
into STRICT	qt_itens_analise_w
from (SELECT	count(1) qt_itens
	from	pls_guia_plano_proc
	where	nr_seq_guia = nr_seq_guia_p
	and	coalesce(nr_seq_motivo_exc::text, '') = ''
	and	ie_status = 'A'
	
union

	SELECT	count(1) qt_itens
	from	pls_guia_plano_mat
	where	nr_seq_guia = nr_seq_guia_p
	and	coalesce(nr_seq_motivo_exc::text, '') = ''
	and	ie_status = 'A') alias5;

if (ie_opcao_p = 'A') then
	if (qt_itens_analise_w > 0) then
		ds_retorno_w := 'S';
	end if;
elsif (ie_opcao_p = 'L') then
	if (qt_itens_aprovado_w > 0) then
		ds_retorno_w := 'S';
	end if;
elsif (ie_opcao_p = 'N') then
	if (qt_itens_negados_w > 0) then
		ds_retorno_w := 'S';
	end if;
elsif (ie_opcao_p = 'AT') then
	if (qt_itens_analise_w = qt_itens_w) then
		ds_retorno_w := 'S';
	end if;
elsif (ie_opcao_p = 'LT') then
	if (qt_itens_aprovado_w = qt_itens_w) then
		ds_retorno_w := 'S';
	end if;
elsif (ie_opcao_p = 'NT') then
	if (qt_itens_negados_w = qt_itens_w) then
		ds_retorno_w := 'S';
	end if;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_itens_guia_status ( nr_seq_guia_p bigint, ie_opcao_p text) FROM PUBLIC;

