SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_itens_lib_usuario ( nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


qt_proc_aprov_w			bigint;
qt_mat_aprov_w			bigint;
ie_retorno_w			varchar(2)	:= 'N';


BEGIN

select	count(1)
into STRICT	qt_proc_aprov_w
from	pls_guia_plano_proc
where	ie_status	in ('L','S')
and	nr_seq_guia	= nr_seq_guia_p;

select	count(1)
into STRICT	qt_mat_aprov_w
from	pls_guia_plano_mat
where	ie_status	in ('L','S')
and	nr_seq_guia	= nr_seq_guia_p;

if (qt_proc_aprov_w	> 0) or (qt_mat_aprov_w	> 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
