CREATE OR REPLACE FUNCTION pls_obter_se_liminar_segurado ( nr_seq_liminar_p processo_judicial_liminar.nr_sequencia%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1);
nr_seq_contrato_w		pls_contrato.nr_sequencia%type;
cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;
nr_seq_segurado_liminar_w	pls_segurado.nr_sequencia%type;
cd_pessoa_liminar_w		pessoa_fisica.cd_pessoa_fisica%type;
ie_considera_codigo_pf_w	processo_judicial_liminar.ie_considera_codigo_pf%type;
nr_seq_contrato_liminar_w	pls_contrato.nr_sequencia%type;


BEGIN

select	nr_seq_contrato,
	cd_pessoa_fisica
into STRICT	nr_seq_contrato_w,
	cd_pessoa_fisica_w
from	pls_segurado
where	nr_sequencia = nr_seq_segurado_p;

select	a.nr_seq_segurado,
	(	select	x.cd_pessoa_fisica
		from	pls_segurado x
		where	x.nr_sequencia	= a.nr_seq_segurado) cd_pessoa_liminar,
	coalesce(a.ie_considera_codigo_pf,'N'),
	a.nr_seq_contrato
into STRICT	nr_seq_segurado_liminar_w,
	cd_pessoa_liminar_w,
	ie_considera_codigo_pf_w,
	nr_seq_contrato_liminar_w
from	processo_judicial_liminar a
where	a.nr_sequencia = nr_seq_liminar_p;

if (nr_seq_segurado_liminar_w = nr_seq_segurado_p) or (nr_seq_contrato_liminar_w = nr_seq_contrato_w) or (ie_considera_codigo_pf_w = 'S' and cd_pessoa_liminar_w = cd_pessoa_fisica_w) then
	ds_retorno_w	:= 'S';
else
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
