CREATE OR REPLACE FUNCTION pls_obter_se_limit_req ( nr_seq_segurado_p bigint, nr_seq_tipo_limitacao_p bigint, qt_permitida_p bigint, qt_solicitada_p bigint, nr_seq_requisicao_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ie_limitacao_w			varchar(1);
nr_seq_req_proc_w		bigint;
qt_liberada_w			double precision := 0;
qt_autorizada_w			double precision;
qt_permitida_w			double precision;
nr_seq_req_mat_w		bigint;

C01 CURSOR FOR
	SELECT	pls_obter_regra_limitacao(a.cd_procedimento,null,a.ie_origem_proced,nr_seq_segurado_p,null),
		sum(coalesce(qt_procedimento,0))
	from	pls_requisicao_proc a
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	and	pls_obter_regra_limitacao(a.cd_procedimento,null,a.ie_origem_proced,nr_seq_segurado_p,null) = nr_seq_tipo_limitacao_p
	and	a.ie_status in ('L','S')
	group	by pls_obter_regra_limitacao(a.cd_procedimento,null,a.ie_origem_proced,nr_seq_segurado_p,null);

c02 CURSOR FOR
	SELECT	pls_obter_regra_limitacao(null,a.nr_seq_material,null,nr_seq_segurado_p,null),
		sum(coalesce(qt_material,0))
	from	pls_requisicao_mat a
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	and	pls_obter_regra_limitacao(null,a.nr_seq_material,null,nr_seq_segurado_p,null) = nr_seq_tipo_limitacao_p
	and	a.ie_status in ('L','S')
	group	by pls_obter_regra_limitacao(null,a.nr_seq_material,null,nr_seq_segurado_p,null);

BEGIN

ie_limitacao_w	:= 'N';
qt_permitida_w	:= coalesce(qt_permitida_p,0);

if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	if (ie_tipo_item_p	= 'P') then
		open c01;
		loop
		fetch c01 into
			nr_seq_req_proc_w,
			qt_autorizada_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			qt_liberada_w	:= coalesce(qt_liberada_w,0) + coalesce(qt_autorizada_w,0);
		end loop;
		close c01;
	elsif (ie_tipo_item_p	= 'M')	then
		open c02;
		loop
		fetch c02 into
			nr_seq_req_mat_w,
			qt_autorizada_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			qt_liberada_w	:= coalesce(qt_liberada_w,0) + coalesce(qt_autorizada_w,0);
		end loop;
		close c02;
	end if;

	qt_liberada_w	:= qt_liberada_w + coalesce(qt_solicitada_p,0);

	if (qt_permitida_w < qt_liberada_w) then
		ie_limitacao_w	:= 'S';
	end if;

end if;

return ie_limitacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
