CREATE OR REPLACE FUNCTION pls_obter_se_material_excluido ( nr_seq_material_p bigint, nr_seq_guia_req_p bigint, ie_funcao_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_funcao_p
AU - OPS - Autorizações
GA - OPS - GEstão de Análise de Autorizações
RA  - OPS - Requisições para Autorização
*/
ie_retorno_w			varchar(2)	:= 'N';
dt_solicitacao_w		timestamp;
dt_exclusao_w			pls_material.dt_exclusao%type;
dt_limite_utilizacao_w		pls_material.dt_limite_utilizacao%type;


BEGIN

if (coalesce(nr_seq_guia_req_p,0)	<> 0) and (coalesce(nr_seq_material_p,0)	<> 0) then
	if (ie_funcao_p	= 'AU') then
		select	trunc(dt_solicitacao)
		into STRICT	dt_solicitacao_w
		from	pls_guia_plano
		where	nr_sequencia	= nr_seq_guia_req_p;

	elsif (ie_funcao_p	= 'GA') then
		dt_solicitacao_w	:= trunc(clock_timestamp());

	elsif (ie_funcao_p	= 'RA') then
		select	trunc(dt_requisicao)
		into STRICT	dt_solicitacao_w
		from	pls_requisicao
		where	nr_sequencia = nr_seq_guia_req_p;
	end if;

	select	trunc(dt_exclusao),
		trunc(dt_limite_utilizacao)
	into STRICT	dt_exclusao_w,
		dt_limite_utilizacao_w
	from 	pls_material
	where 	nr_sequencia 	= nr_seq_material_p;

	if (coalesce(dt_exclusao_w::text, '') = '') and (coalesce(dt_limite_utilizacao_w::text, '') = '') then
		ie_retorno_w	:= 'N';
	elsif (dt_solicitacao_w <= dt_limite_utilizacao_w) then
		ie_retorno_w	:= 'N';
	elsif (dt_solicitacao_w <= dt_exclusao_w) then
		ie_retorno_w	:= 'N';
	else
		ie_retorno_w	:= 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
