CREATE OR REPLACE FUNCTION pls_obter_se_mat_atend ( nr_seq_material_p bigint, nr_seq_proc_tipo_atend_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1);
nr_seq_estrut_mat_w		numeric(30);


BEGIN

-- jjung OS 491904 07/09/2012: obter a estrutura do material
select	coalesce(nr_seq_estrut_mat,0)
into STRICT	nr_seq_estrut_mat_w
from	pls_material
where	nr_sequencia = nr_seq_material_p;

select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	pls_oc_mat_atend_regra
where	nr_seq_material		= nr_seq_material_p
and	nr_seq_regra		= nr_seq_proc_tipo_atend_p;

-- jjung OS 491904 07/09/2012: Se n√£o existir regra para o material, verificar para a estrutura
if (coalesce(ds_retorno_w,'N') = 'N') then
	select	CASE WHEN coalesce(count(1),0)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	pls_oc_mat_atend_regra
	where	nr_seq_estrutura_mat	= nr_seq_estrut_mat_w
	and	nr_seq_regra		= nr_seq_proc_tipo_atend_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_mat_atend ( nr_seq_material_p bigint, nr_seq_proc_tipo_atend_p bigint) FROM PUBLIC;

