CREATE OR REPLACE FUNCTION pls_obter_se_mat_estrutura ( nr_seq_material_p pls_material.nr_sequencia%type, nr_seq_estrutura_p pls_estrutura_material.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'N';
qt_registro_w		integer;


BEGIN

if (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') and (nr_seq_estrutura_p IS NOT NULL AND nr_seq_estrutura_p::text <> '') then

	-- verifica se a estrutura passada de parâmetro (nr_seq_estrutura_p) está na estrutura
	-- ou na árvore da estrutura do material
	-- usada a tabela tm que contém toda a árvore de estrutura do material
	select	count(1)
	into STRICT	qt_registro_w
	from	pls_estrutura_material_tm
	where	nr_seq_estrutura = nr_seq_estrutura_p
	and	nr_seq_material = nr_seq_material_p;

	if (qt_registro_w > 0) then
		ie_retorno_w		:= 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
