SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_mat_estrut_conv ( nr_seq_material_p pls_material.nr_sequencia%type, nr_seq_estrutura_p pls_estrutura_material.nr_sequencia%type, cd_material_p pls_material.cd_material_ops%type) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'N';
qt_registro_w		integer;
	

BEGIN

if (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') and (nr_seq_estrutura_p IS NOT NULL AND nr_seq_estrutura_p::text <> '') then
	
	-- verifica se a estrutura passada de parâmetro (nr_seq_estrutura_p) está na estrutura

	-- ou na árvore da estrutura do material

	-- usada a tabela tm que contém toda a árvore de estrutura do material
	select	count(1)
	into STRICT	qt_registro_w
	from	pls_estrutura_material_tm
	where	nr_seq_estrutura = nr_seq_estrutura_p
	and	nr_seq_material = nr_seq_material_p;
	
	-- Caso não encontre pela sequencia do material busca pelo código do materail na operadora
	if (qt_registro_w = 0) then
		select	count(1)
		into STRICT	qt_registro_w
		from	pls_estrutura_material_tm a
		where	a.nr_seq_estrutura = nr_seq_estrutura_p
		and	exists (	SELECT	1
				from	pls_material x
				where	x.nr_sequencia = a.nr_seq_material
				and	cd_material_ops = cd_material_p);
		
		-- Busca pelo código brasindice
		if (qt_registro_w = 0) then
			select	count(1)
			into STRICT	qt_registro_w
			from	pls_estrutura_material_tm a
			where	a.nr_seq_estrutura = nr_seq_estrutura_p
			and	exists (	SELECT	1
					from	pls_material x
					where	x.nr_sequencia = a.nr_seq_material
					and	x.cd_tiss_brasindice = cd_material_p);
			
			-- Busca pelo código Simpro
			if (qt_registro_w = 0) then
							
				begin
				
					select	count(1)
					into STRICT	qt_registro_w
					from	pls_estrutura_material_tm a
					where	a.nr_seq_estrutura = nr_seq_estrutura_p
					and	exists (	SELECT	1
							from	pls_material x
							where	x.nr_sequencia = a.nr_seq_material
							and	x.cd_simpro = cd_material_p );
							
				exception
				when others then
					qt_registro_w := 0;
				end;
					
				-- Busca pelo código material TUSS

				-- Colocado por último pois é o mais custoso
				if (qt_registro_w = 0) then
				
					begin
						select	count(1)
						into STRICT	qt_registro_w
						from	pls_estrutura_material_tm a
						where	a.nr_seq_estrutura = nr_seq_estrutura_p
						and	exists (	SELECT	1
								from	pls_material x,
									tuss_material_item y
								where	y.nr_sequencia = x.nr_seq_tuss_mat_item
								and	x.nr_sequencia = a.nr_seq_material
								and	y.cd_material_tuss = cd_material_p );
					exception
					when others then
						qt_registro_w := 0;
					end;
				
				end if;				
			end if;
		end if;
	end if;
	
	-- Caso encontre, é válido
	if (qt_registro_w > 0) then
		ie_retorno_w		:= 'S';
	end if;
end if;
	
return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_mat_estrut_conv ( nr_seq_material_p pls_material.nr_sequencia%type, nr_seq_estrutura_p pls_estrutura_material.nr_sequencia%type, cd_material_p pls_material.cd_material_ops%type) FROM PUBLIC;

