CREATE OR REPLACE FUNCTION pls_obter_se_mat_inativo ( nr_seq_material_p bigint, dt_material_p timestamp) RETURNS varchar AS $body$
DECLARE

				
ds_retorno_w			varchar(1);
ie_mat_valido_w			varchar(1) := 'S';


BEGIN

begin
	select	coalesce(max('S'),'N')
	into STRICT	ie_mat_valido_w
	from	pls_material
	where	nr_sequencia = nr_seq_material_p
	and	trunc(dt_material_p) between trunc(dt_inclusao) and trunc(coalesce(coalesce(dt_limite_utilizacao,dt_exclusao),dt_material_p));
exception
when others then
	ie_mat_valido_w := 'S';
end;

ds_retorno_w	:= ie_mat_valido_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
