SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_med_prestador (cd_medico_p text, nr_seq_prestador_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);
cont_w			bigint;
cd_pessoa_fisica_w	varchar(11);
cd_cgc_w		varchar(14);


BEGIN

cont_w := 0;

select	max(cd_pessoa_fisica),
	max(cd_cgc)
into STRICT	cd_pessoa_fisica_w,
	cd_cgc_w
from	pls_prestador
where	nr_sequencia	= nr_seq_prestador_p;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	select	count(*)
	into STRICT	cont_w
	from	pessoa_fisica
	where	cd_pessoa_fisica_w = cd_medico_p;
elsif (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
	select	count(*)
	into STRICT	cont_w
	from	pls_prestador_medico a,
		pls_prestador b
	where	a.nr_seq_prestador	= b.nr_sequencia
	and	a.cd_medico		= cd_medico_p
	and	a.nr_seq_prestador	= nr_seq_prestador_p
	and	a.ie_situacao		= 'A'
	and	trunc(clock_timestamp(),'dd') between trunc(coalesce(a.dt_inclusao,clock_timestamp()),'dd') and  fim_dia(coalesce(a.dt_exclusao,clock_timestamp()));
end if;

if (cont_w = 0) then
	ie_retorno_w :=	'N';
else
	ie_retorno_w := 'S';
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
