CREATE OR REPLACE FUNCTION pls_obter_se_mens_gerada ( nr_seq_lote_p bigint, qt_meses_ant_p bigint) RETURNS varchar AS $body$
DECLARE


ie_gerado_w		varchar(1) := 'S';/* "S" para todos os beneficiários gerados e "N" quando algum não foi gerado */
dt_mes_competencia_w	timestamp;
dt_mesano_referencia_w	timestamp;
nr_seq_contrato_w	bigint;
nr_seq_segurado_w	bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_segurado a
	where	coalesce(a.dt_rescisao::text, '') = ''
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	pls_obter_se_mens_segurado(a.nr_sequencia, add_months(trunc(dt_mesano_referencia_w,'month'),-(qt_meses_ant_p+1)), trunc(dt_mesano_referencia_w,'month')) = 'N'
	and	coalesce(nr_seq_contrato_w,nr_seq_contrato) = nr_seq_contrato
	and	ie_gerado_w = 'S';


BEGIN

select	trunc(add_months(dt_mesano_referencia,-1),'month'),
	nr_seq_contrato
into STRICT	dt_mesano_referencia_w,
	nr_seq_contrato_w
from	pls_lote_mensalidade
where	nr_sequencia = nr_seq_lote_p;

open C01;
loop
fetch C01 into
	nr_seq_segurado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ie_gerado_w := 'N';
end loop;
close C01;

return	ie_gerado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
