CREATE OR REPLACE FUNCTION pls_obter_se_mens_segurado ( nr_seq_segurado_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE

/* Procedure criada para retornar se o usuário possui mensalidades não geradas no período do parâmetro */

qt_parcelas_w		bigint;
dt_inicial_w		timestamp;
dt_adesao_w		timestamp;
qt_meses_vigente_w	integer;
ie_gerado_w		varchar(1);
ie_verifica_w		varchar(1)	:= 'S';


BEGIN

begin
select	a.dt_contratacao
into STRICT	dt_adesao_w
from	pls_segurado a,
	pls_contrato b
where	a.nr_seq_contrato	= b.nr_sequencia
and	a.nr_sequencia		= nr_seq_segurado_p
and	((b.ie_geracao_valores	= 'T' and coalesce(a.nr_seq_titular::text, '') = '')
	or (b.ie_geracao_valores	= 'D' and (a.nr_seq_titular IS NOT NULL AND a.nr_seq_titular::text <> ''))
	or (b.ie_geracao_valores	= 'B'));
exception
	when no_data_found then
		ie_verifica_w	:= 'N';
end;

if (ie_verifica_w	= 'S') then

	if (trunc(dt_adesao_w,'month') <= dt_inicial_p) then
		dt_inicial_w	:= trunc(dt_inicial_p,'month');
	else
		dt_inicial_w	:= trunc(dt_adesao_w,'month');
	end if;

	qt_meses_vigente_w	:= months_between(trunc(dt_final_p,'month'),dt_inicial_w) + 1;

	select	count(*)
	into STRICT	qt_parcelas_w
	from	pls_mensalidade_seg_item c,
		pls_mensalidade_segurado b,
		pls_mensalidade a
	where	a.nr_sequencia	= b.nr_seq_mensalidade
	and	b.nr_sequencia	= c.nr_seq_mensalidade_seg
	and	trunc(b.dt_mesano_referencia,'month') between trunc(dt_inicial_p,'month') and trunc(dt_final_p,'month')
	and	b.nr_seq_segurado	= nr_seq_segurado_p
	and	coalesce(a.ie_cancelamento::text, '') = ''
	and	c.ie_tipo_item = '1'
	and	coalesce(c.ie_tipo_mensalidade,'N') = 'N';

	if (qt_meses_vigente_w <> qt_parcelas_w) then
		ie_gerado_w	:= 'N';
	else
		ie_gerado_w	:= 'S';
	end if;
else
	ie_gerado_w := 'S';
end if;

return	ie_gerado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
