SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_mens_seg ( nr_seq_segurado_p bigint, nr_seq_mensalidade_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1)	:= 'S';
dt_adesao_w			timestamp;
dt_mesano_referencia_w		timestamp;
nr_seq_contrato_w		bigint;
dt_limite_movimentacao_w	varchar(2);
dt_inicio_movimentacao_w	timestamp;
dt_fim_movimentacao_w		timestamp;
nr_seq_regra_w			bigint;
ie_tipo_data_limite_w		varchar(2);
vl_variacao_mes_w		integer;
ie_tipo_contrato_w		varchar(2);
nr_seq_intercambio_w		bigint;
cd_estabelecimento_w		smallint;


BEGIN

select	dt_contratacao,
	nr_seq_contrato,
	nr_seq_intercambio
into STRICT	dt_adesao_w,
	nr_seq_contrato_w,
	nr_seq_intercambio_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

select	CASE WHEN coalesce(cd_pf_estipulante::text, '') = '' THEN 'PJ'  ELSE 'PF' END ,
	cd_estabelecimento
into STRICT	ie_tipo_contrato_w,
	cd_estabelecimento_w
from	pls_contrato
where	nr_sequencia	= nr_seq_contrato_w;

select	dt_referencia
into STRICT	dt_mesano_referencia_w
from	pls_mensalidade
where	nr_sequencia	= nr_seq_mensalidade_p;

if (coalesce(nr_seq_contrato_w,0) <> 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	pls_regra_mens_contrato
	where	nr_seq_contrato	= nr_seq_contrato_w
	and	ie_tipo_regra	= 'L'
	and	trunc(dt_mesano_referencia_w,'dd')	>= trunc(dt_inicio_vigencia,'dd')
	and	trunc(dt_mesano_referencia_w,'dd')	<= trunc(coalesce(dt_fim_vigencia,dt_mesano_referencia_w),'dd');
elsif (coalesce(nr_seq_intercambio_w,0) <> 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	pls_regra_mens_contrato
	where	nr_seq_intercambio	= nr_seq_intercambio_w
	and	ie_tipo_regra		= 'L'
	and	trunc(dt_mesano_referencia_w,'dd')	>= trunc(dt_inicio_vigencia,'dd')
	and	trunc(dt_mesano_referencia_w,'dd')	<= trunc(coalesce(dt_fim_vigencia,dt_mesano_referencia_w),'dd');
end if;

if (coalesce(nr_seq_regra_w,0) = 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	pls_regra_mens_contrato
	where	coalesce(nr_seq_contrato::text, '') = ''
	and	coalesce(nr_seq_intercambio::text, '') = ''
	and	ie_tipo_regra		= 'L'
	and	cd_estabelecimento	= cd_estabelecimento_w
	and	trunc(dt_mesano_referencia_w,'dd')	>= trunc(dt_inicio_vigencia,'dd')
	and	trunc(dt_mesano_referencia_w,'dd')	<= trunc(coalesce(dt_fim_vigencia,dt_mesano_referencia_w),'dd')
	and	((coalesce(ie_pessoa_contrato,'A') = ie_tipo_contrato_w) or (ie_pessoa_contrato = 'A'));
end if;

if (coalesce(nr_seq_regra_w,0)	> 0) then
	select	lpad(to_char(coalesce(a.dt_limite_movimentacao,'0')),2,0),
		a.ie_tipo_data_limite
	into STRICT	dt_limite_movimentacao_w,
		ie_tipo_data_limite_w
	from	pls_regra_mens_contrato a
	where	a.nr_sequencia	= nr_seq_regra_w;
end if;

if (dt_limite_movimentacao_w <> '00') then

	select	CASE WHEN ie_tipo_data_limite_w='C' THEN 0 WHEN ie_tipo_data_limite_w='A' THEN -1 END
	into STRICT	vl_variacao_mes_w
	;

	begin
	dt_fim_movimentacao_w		:= add_months(to_date(dt_limite_movimentacao_w || to_char(dt_mesano_referencia_w,'mm/yyyy')),vl_variacao_mes_w);
	exception
	when others then
		dt_fim_movimentacao_w	:= add_months(last_day(dt_mesano_referencia_w),vl_variacao_mes_w);
	end;

	dt_inicio_movimentacao_w	:= add_months(dt_fim_movimentacao_w,-1) + 1;

	if (dt_adesao_w	> dt_fim_movimentacao_w) then
		ds_retorno_w	:= 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
