CREATE OR REPLACE FUNCTION pls_obter_se_meta_atingida ( nr_seq_vendedor_p bigint, dt_referencia_p timestamp, qt_meses_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
dt_mes_competencia_w	timestamp;
nr_seq_meta_w		bigint;
qt_meta_w		bigint;
qt_vidas_w		bigint;

C01 CURSOR FOR
	SELECT	distinct dt_mes_competencia
	from	pls_competencia
	where	dt_mes_competencia < trunc(dt_referencia_p,'month')
	order by dt_mes_competencia desc LIMIT (qt_meses_p);


BEGIN

if (coalesce(qt_meses_p,0) > 0) then
	open C01;
	loop
	fetch C01 into
		dt_mes_competencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		dt_mes_competencia_w 	:= trunc(dt_mes_competencia_w,'month');

		select	sum(qt_vidas)
		into STRICT	qt_vidas_w
		from	pls_repasse_vend
		where	trunc(dt_referencia,'month') = dt_mes_competencia_w
		and	nr_seq_vendedor = nr_seq_vendedor_p;

		select	max(nr_sequencia)
		into STRICT	nr_seq_meta_w
		from	pls_vendedor_meta
		where	trunc(dt_mesano_referencia,'month') = dt_mes_competencia_w
		and	nr_seq_vendedor = nr_seq_vendedor_p;

		if (coalesce(nr_seq_meta_w,0) > 0) then
			select	qt_meta
			into STRICT	qt_meta_w
			from	pls_vendedor_meta
			where	nr_sequencia = nr_seq_meta_w;

			if (coalesce(qt_vidas_w,0) >= qt_meta_w) then
				ds_retorno_w	:= 'S';
			else
				ds_retorno_w	:= 'N';
				exit;
			end if;
		else
			if (coalesce(qt_vidas_w,0) > 0) then
				ds_retorno_w	:= 'M';
				exit;
			else
				ds_retorno_w	:= 'S';
			end if;
		end if;

		end;
	end loop;
	close C01;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
