CREATE OR REPLACE FUNCTION pls_obter_se_motivo_canc ( nr_seq_motivo_cancelamento_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
qt_registros_w		bigint;

C01 CURSOR FOR
	SELECT	coalesce(ie_permite_acesso,'S') ie_permite_acesso
	from	pls_motivo_cancel_lib
	where	nr_seq_motivo	= nr_seq_motivo_cancelamento_p
	and	((cd_perfil	= cd_perfil_p) or (coalesce(cd_perfil::text, '') = ''))
	and	((upper(nm_usuario_lib)	= upper(nm_usuario_p)) or (coalesce(nm_usuario_lib::text, '') = ''))
	order by coalesce(nm_usuario_lib,' '),
		coalesce(cd_perfil,0);

BEGIN
select	count(1)
into STRICT	qt_registros_w
from	pls_motivo_cancel_lib
where	nr_seq_motivo	= nr_seq_motivo_cancelamento_p  LIMIT 1;

if (qt_registros_w = 0) then
	return 'S';
else
	ds_retorno_w	:= 'N';
end if;

for r_c01_w in C01 loop
	begin
	ds_retorno_w	:= r_c01_w.ie_permite_acesso;
	end;
end loop;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
