CREATE OR REPLACE FUNCTION pls_obter_se_municipio_diops (nr_seq_operadora_p bigint, cd_municipio_ibge_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(255) := 'S';
nr_seq_outorgante_w	bigint;
cont_w			bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	pls_regiao_atuacao
where	nr_seq_operadora	= nr_seq_operadora_p
and	(cd_municipio_ibge IS NOT NULL AND cd_municipio_ibge::text <> '');

if (cont_w > 0) then

	ie_retorno_w			:= 'N';
	select	count(*)
	into STRICT	cont_w
	from	pls_regiao_atuacao
	where	nr_seq_operadora 	= nr_seq_operadora_p
	and	cd_municipio_ibge	= cd_municipio_ibge_p;

	if (cont_w > 0) then
		ie_retorno_w		:= 'S';
	end if;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
