CREATE OR REPLACE FUNCTION pls_obter_se_necessario_glosar ( qt_liberada_p bigint, qt_apresentada_p bigint) RETURNS varchar AS $body$
DECLARE

/*
	CHAMADA NO FONTE DA CORPLS_AC
*/
vl_retorno_w		varchar(1):= 'S'; --Por padrão sempre será necessário glosar
BEGIN

/*VERIFICA QTDE GLOSA SE ZERADA*/

if (coalesce(qt_liberada_p,0) > 0) and (coalesce(qt_apresentada_p,0) > 0) and (qt_liberada_p = qt_apresentada_p) then

	vl_retorno_w	:= 'N';	-- Se qtde glossa zerado , então não precisa glosar.
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
