CREATE OR REPLACE FUNCTION pls_obter_se_nivel_lib_auditor ( nr_seq_ocorrencia_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text, ie_consistir_grupo_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[ X ]  Objetos do dicionario [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_retorno_w			varchar(1)	:= 'S';
nr_seq_nivel_lib_w		bigint;
nr_nivel_liberacao_w		bigint;
nr_seq_nivel_lib_aud_w		bigint;
nr_nivel_liberacao_aud_w	bigint;


BEGIN
if (coalesce(nr_seq_ocorrencia_p,0) > 0) and (coalesce(ie_consistir_grupo_p,'S') = 'S') then
	/*Nivel de liberacao da ocorrencia*/

	select	a.nr_seq_nivel_lib
	into STRICT	nr_seq_nivel_lib_w
	from	pls_ocorrencia a
	where	a.nr_sequencia	= nr_seq_ocorrencia_p;

	select	max(nr_nivel_liberacao)
	into STRICT	nr_nivel_liberacao_w
	from	pls_nivel_liberacao
	where	nr_sequencia	= nr_seq_nivel_lib_w;

	begin
	select	coalesce(a.nr_seq_nivel_lib,b.nr_seq_nivel_lib)
	into STRICT	nr_seq_nivel_lib_aud_w
	from	pls_membro_grupo_aud	a,
		pls_grupo_auditor	b	
	where	a.nr_seq_grupo 		= nr_seq_grupo_p
	and	a.nm_usuario_exec 	= nm_usuario_p
	and	a.nr_seq_grupo 		= b.nr_sequencia;
	exception
	when others then
		nr_seq_nivel_lib_aud_w	:= null;
	end;
	
	select	max(nr_nivel_liberacao)
	into STRICT	nr_nivel_liberacao_aud_w
	from	pls_nivel_liberacao
	where	nr_sequencia	= nr_seq_nivel_lib_aud_w;
	
	if (coalesce(nr_nivel_liberacao_aud_w,0) < coalesce(nr_nivel_liberacao_w,0)) then
		ie_retorno_w	:= 'N';
	end if;
elsif (coalesce(nr_seq_ocorrencia_p,0) > 0) then
	select	max(a.nr_seq_nivel_lib)
	into STRICT	nr_seq_nivel_lib_w
	from	pls_ocorrencia a
	where	a.nr_sequencia	= nr_seq_ocorrencia_p
	and	((exists (SELECT	1
			from	pls_ocorrencia_grupo	b
			where	b.nr_seq_ocorrencia	= a.nr_sequencia
			and	b.nr_seq_grupo		= nr_seq_grupo_p
			and	b.ie_situacao		= 'A')) or (not exists (select	1
			from	pls_ocorrencia_grupo	b
			where	b.nr_seq_ocorrencia	= a.nr_sequencia
			and	b.ie_situacao		= 'A')));

	select	max(nr_nivel_liberacao)
	into STRICT	nr_nivel_liberacao_w
	from	pls_nivel_liberacao
	where	nr_sequencia	= nr_seq_nivel_lib_w;

	begin
		select	coalesce(a.nr_seq_nivel_lib,b.nr_seq_nivel_lib)
		into STRICT	nr_seq_nivel_lib_aud_w
		from	pls_membro_grupo_aud	a,
			pls_grupo_auditor	b	
		where	a.nr_seq_grupo 		= nr_seq_grupo_p
		and	a.nm_usuario_exec 	= nm_usuario_p
		and	a.nr_seq_grupo 		= b.nr_sequencia;
	exception
	when others then
		nr_seq_nivel_lib_aud_w	:= null;
	end;
	
	select	max(nr_nivel_liberacao)
	into STRICT	nr_nivel_liberacao_aud_w
	from	pls_nivel_liberacao
	where	nr_sequencia	= nr_seq_nivel_lib_aud_w;
	
	if (coalesce(nr_nivel_liberacao_aud_w,0) < coalesce(nr_nivel_liberacao_w,0)) then
		ie_retorno_w	:= 'N';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_nivel_lib_auditor ( nr_seq_ocorrencia_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text, ie_consistir_grupo_p text) FROM PUBLIC;

