CREATE OR REPLACE FUNCTION pls_obter_se_nm_usuario_plano ( cd_estabelecimento_p bigint, cd_usuario_plano_p text) RETURNS varchar AS $body$
DECLARE

 
cd_usuario_w		pls_segurado_carteira.nr_seq_segurado%type;
nm_usuario_w		varchar(2000);


BEGIN 
if (cd_usuario_plano_p IS NOT NULL AND cd_usuario_plano_p::text <> '') then 
	select	max(coalesce(a.nr_seq_segurado,0)) 
	into STRICT	cd_usuario_w 
	from	pls_segurado      b, 
		pls_segurado_carteira  a 
	where	a.nr_seq_segurado  = b.nr_sequencia 
	and	a.cd_usuario_plano  = cd_usuario_plano_p 
	and	b.cd_estabelecimento = cd_estabelecimento_p;
 
	select	substr(pls_obter_dados_segurado(cd_usuario_w,'N'),1,255) 
	into STRICT	nm_usuario_w 
	;
end if;
 
return nm_usuario_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
