CREATE OR REPLACE FUNCTION pls_obter_se_nota_contestada ( nr_fatura_p bigint, nr_nota_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';


BEGIN

select	max('S')
into STRICT	ds_retorno_w
from	ptu_questionamento a,
	ptu_camara_contestacao b,
	ptu_questionamento_codigo c
where	a.nr_seq_contestacao = b.nr_sequencia
and	c.nr_seq_registro	 = a.nr_sequencia
and (SELECT		max(d.cd_motivo)
	 from		ptu_motivo_questionamento d
	 where		d.nr_sequencia = c.nr_seq_mot_questionamento) <> 99
and	b.nr_fatura = nr_fatura_p
and	a.nr_nota = nr_nota_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
