CREATE OR REPLACE FUNCTION pls_obter_se_ocorrencia_simul ( nr_seq_simultaneo_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_tipo_item_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ie_simultaneo_w			varchar(1) := 'N';
cd_procedimento_w		bigint := 0;
ie_origem_proced_w		bigint;
nr_seq_guia_w			bigint;
nr_seq_conta_w			bigint;
qt_contador_simul_w		integer := 0;
qt_proc_w			integer;
qt_proc_simul_w			integer := 0;
qt_item_proc_simul_w		integer := 0;

C01 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced
	from	pls_guia_plano_proc
	where	nr_seq_guia = nr_seq_guia_w
	order by 1;

C02 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced
	from	pls_conta_proc
	where	nr_seq_conta = nr_seq_conta_w
	order by 1;
BEGIN

select	count(1)
into STRICT	qt_proc_simul_w
from	pls_ocorrencia_simul_item
where	nr_seq_regra		= nr_seq_simultaneo_p
and	cd_procedimento 	= cd_procedimento_p
and	ie_origem_proced 	= ie_origem_proced_p
and	ie_ocorrencia		= 'S'
and	ie_situacao 		= 'A';

if (qt_proc_simul_w > 0) then
	if (ie_tipo_item_p = 1) then
		select	nr_seq_guia
		into STRICT	nr_seq_guia_w
		from	pls_guia_plano_proc
		where	nr_sequencia = nr_seq_item_p;

		for r_c01_w in C01() loop
			begin

			select	count(1)
			into STRICT	qt_proc_w
			from	pls_ocorrencia_simul_item
			where	nr_seq_regra		= nr_seq_simultaneo_p
			and	cd_procedimento 	= r_c01_w.cd_procedimento
			and	ie_origem_proced	= r_c01_w.ie_origem_proced
			and	ie_situacao 		= 'A';

			qt_contador_simul_w := qt_contador_simul_w + qt_proc_w;
			end;
		end loop;

		select	count(1)
		into STRICT	qt_item_proc_simul_w
		from	pls_guia_plano_proc
		where	nr_seq_guia = nr_seq_guia_w;
	elsif (ie_tipo_item_p = 3) then
		select	nr_seq_conta
		into STRICT	nr_seq_conta_w
		from	pls_conta_proc
		where	nr_sequencia = nr_seq_item_p;

		for r_c02_w in C02() loop
			begin
			select	count(1)
			into STRICT	qt_proc_w
			from	pls_ocorrencia_simul_item
			where	nr_seq_regra		= nr_seq_simultaneo_p
			and	cd_procedimento 	= r_c02_w.cd_procedimento
			and	ie_origem_proced 	= r_c02_w.ie_origem_proced
			and	ie_situacao = 'A';

			qt_contador_simul_w := qt_contador_simul_w + qt_proc_w;

			end;
		end loop;

		select	count(1)
		into STRICT	qt_item_proc_simul_w
		from	pls_conta_proc
		where	nr_seq_conta = nr_seq_conta_w;
	end if;

	select	count(1)
	into STRICT	qt_proc_simul_w
	from	pls_ocorrencia_simul_item
	where	nr_seq_regra	= nr_seq_simultaneo_p;

	if ( qt_item_proc_simul_w = qt_contador_simul_w ) and ( qt_proc_simul_w = qt_contador_simul_w ) then
		ie_simultaneo_w := 'S';
	end if;
end if;

return	ie_simultaneo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
