CREATE OR REPLACE FUNCTION pls_obter_se_ocorr_acao_ptu ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, nr_seq_complemento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_transacao_w				varchar(2);
ie_retorno_w					varchar(2)	:= 'N';

nr_seq_ocorrencia_w				bigint;
nr_seq_procedimento_w				bigint;
nr_seq_material_w				bigint;

qt_registros_w					bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_guia_plano_proc
	where	nr_seq_guia		= nr_seq_guia_p
	and (nr_seq_compl_ptu	= nr_seq_complemento_p	or coalesce(nr_seq_complemento_p::text, '') = '')
	
union

	SELECT	nr_sequencia
	from	pls_requisicao_proc
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	and (nr_seq_compl_ptu	= nr_seq_complemento_p	or coalesce(nr_seq_complemento_p::text, '') = '');

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_guia_plano_mat
	where	nr_seq_guia		= nr_seq_guia_p
	and (nr_seq_compl_ptu	= nr_seq_complemento_p	or coalesce(nr_seq_complemento_p::text, '') = '')
	
union

	SELECT	nr_sequencia
	from	pls_requisicao_mat
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	and (nr_seq_compl_ptu	= nr_seq_complemento_p	or coalesce(nr_seq_complemento_p::text, '') = '');

C03 CURSOR FOR
	SELECT	nr_seq_ocorrencia
	from	pls_ocorrencia_benef
	where	((nr_seq_proc		= nr_seq_procedimento_w)
	or (nr_seq_mat		= nr_seq_material_w))
	and	nr_seq_guia_plano	= nr_seq_guia_p
	and	ie_tipo_transacao_w	= 'G'
	
union

	SELECT	nr_seq_ocorrencia
	from	pls_ocorrencia_benef
	where	((nr_seq_proc		= nr_seq_procedimento_w)
	or (nr_seq_mat		= nr_seq_material_w))
	and	nr_seq_requisicao	= nr_seq_requisicao_p
	and	ie_tipo_transacao_w	= 'R';

BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	ie_tipo_transacao_w	:= 'G';
elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	ie_tipo_transacao_w	:= 'R';
end if;

open C01;
loop
fetch C01 into
	nr_seq_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C03;
	loop
	fetch C03 into
		nr_seq_ocorrencia_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
		begin
		select	count(*)
		into STRICT	qt_registros_w
		from	pls_ocorrencia_scs
		where	nr_seq_ocorrencia	= nr_seq_ocorrencia_w;

		if (qt_registros_w	<> 0) then
			ie_retorno_w	:= 'S';
		end if;
		end;
	end loop;
	close C03;
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_seq_material_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	open C03;
	loop
	fetch C03 into
		nr_seq_ocorrencia_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
		begin
		select	count(*)
		into STRICT	qt_registros_w
		from	pls_ocorrencia_scs
		where	nr_seq_ocorrencia	= nr_seq_ocorrencia_w;

		if (qt_registros_w	<> 0) then
			ie_retorno_w	:= 'S';
		end if;
		end;
	end loop;
	close C03;
	end;
end loop;
close C02;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
