CREATE OR REPLACE FUNCTION pls_obter_se_ocorr_liminar ( nr_seq_auditoria_p pls_auditoria.nr_sequencia%type, nr_seq_aud_item_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(2)	:= 'N';
nr_seq_requisicao_w		pls_requisicao.nr_sequencia%type;
nr_seq_guia_w			pls_guia_plano.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_seq_regra,
			nr_seq_ocorrencia
	from	pls_ocorrencia_benef
	where	nr_seq_requisicao	= nr_seq_requisicao_w
	
union all

	SELECT	nr_seq_regra,
			nr_seq_ocorrencia
	from	pls_ocorrencia_benef
	where	nr_seq_guia_plano	= nr_seq_guia_w;

C02 CURSOR(nr_seq_regra_pc bigint, nr_seq_ocorrencia_pc bigint) FOR
	SELECT	coalesce(ie_possui_liminar,'N') ie_liminar
	from	pls_ocorrencia_regra
	where	nr_sequencia		= nr_seq_regra_pc
	and		nr_seq_ocorrencia 	= nr_seq_ocorrencia_pc
	
union

	SELECT	coalesce(ie_valida_liminar,'N') ie_liminar
	from	pls_ocor_aut_combinada a,
			pls_validacao_aut_liminar b
	where	b.nr_seq_ocor_aut_combinada	= a.nr_sequencia
	and		a.nr_sequencia			= nr_seq_regra_pc
	and		a.nr_seq_ocorrencia 	= nr_seq_ocorrencia_pc
	and		a.ie_aplicacao_regra 	= 30;
BEGIN
begin
	select	nr_seq_requisicao,
			nr_seq_guia
	into STRICT	nr_seq_requisicao_w,
			nr_seq_guia_w
	from	pls_auditoria
	where	nr_sequencia	= nr_seq_auditoria_p;
exception
when others then
	nr_seq_requisicao_w	:= null;
	nr_seq_guia_w		:= null;
end;

for r_c01_w in C01 loop
	for r_c02_w in C02(r_c01_w.nr_seq_regra, r_c01_w.nr_seq_ocorrencia) loop
		ie_retorno_w	:= r_c02_w.ie_liminar;
		if (coalesce(ie_retorno_w,'N')	= 'S') then
			exit;
		end if;
	end loop;
	if (coalesce(ie_retorno_w,'N')	= 'S') then
			exit;
	end if;
end loop;

return	coalesce(ie_retorno_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
