CREATE OR REPLACE FUNCTION pls_obter_se_ocor_benef (nr_seq_ocorrencia_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
qt_resultado_w		bigint;


BEGIN

qt_resultado_w := 0;

select	count(1)
into STRICT	qt_resultado_w
from	pls_ocorrencia_benef	x,
	w_pls_analise_glosa_ocor	y
where	x.nr_sequencia = y.nr_seq_ocor_benef
and	nr_seq_ocorrencia_p = y.nr_seq_ocorrencia
and	y.nm_usuario = nm_usuario_p;

if (coalesce(qt_resultado_w::text, '') = '' or qt_resultado_w = '0') then
	ds_retorno_w := '0';
else
	ds_retorno_w := '1';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
