CREATE OR REPLACE FUNCTION pls_obter_se_ocor_ped_analise ( nr_seq_analise_p bigint, nr_seq_grupo_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Obter se existe ocorrências pendentes ou que necessitam de correção para o grupo de análise em questão
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(1)	:= 'N';
qt_ocorrencia_w		smallint;

BEGIN

select	count(1)
into STRICT	qt_ocorrencia_w
from 	pls_analise_glo_ocor_grupo
where	nr_seq_analise	= nr_seq_analise_p
and	nr_seq_grupo 	= nr_seq_grupo_p
and	ie_status	in ('E', 'P');

if (qt_ocorrencia_w > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
