CREATE OR REPLACE FUNCTION pls_obter_se_oco_sem_grupo ( nr_seq_ocorrencia_benef_p bigint ) RETURNS varchar AS $body$
DECLARE

			
ds_restorno_w			varchar(1)	:= 'S';
nr_seq_ocorrencia_w		bigint;
nr_seq_ocorrencia_grupo_w	bigint;


BEGIN

if (coalesce(nr_seq_ocorrencia_benef_p,0) > 0) then

	select	max(nr_seq_ocorrencia)
	into STRICT	nr_seq_ocorrencia_w
	from	pls_ocorrencia_benef
	where	nr_sequencia	= nr_seq_ocorrencia_benef_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_ocorrencia_grupo_w
	from	pls_ocorrencia_grupo
	where	nr_seq_ocorrencia = nr_seq_ocorrencia_w
	and	ie_situacao	= 'A';
	
	if (coalesce(nr_seq_ocorrencia_grupo_w,0) > 0) then
		ds_restorno_w := 'N';
	end if;
else
	ds_restorno_w := 'N';
end if;

return	ds_restorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
