CREATE OR REPLACE FUNCTION pls_obter_se_oc_possui_regra ( nr_seq_ocorrencia_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';
nr_seq_regra_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_regra_w
from	pls_ocorrencia_regra
where	nr_seq_ocorrencia = nr_seq_ocorrencia_p;

if (coalesce(nr_seq_regra_w,0) > 0) then
	ds_retorno_w := 'S';
else
	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	pls_ocorrencia_regra_item
	where	nr_seq_ocorrencia = nr_seq_ocorrencia_p;

	if (coalesce(nr_seq_regra_w,0) > 0) then
		ds_retorno_w := 'S';
	else
		select	max(nr_sequencia)
		into STRICT	nr_seq_regra_w
		from	pls_oc_regra_pos_estab
		where	nr_seq_ocorrencia = nr_seq_ocorrencia_p;

		if (coalesce(nr_seq_regra_w,0) > 0) then
			ds_retorno_w := 'S';
		else
			select	max(nr_sequencia)
			into STRICT	nr_seq_regra_w
			from	pls_oc_grau_partic_espec
			where	nr_seq_ocorrencia = nr_seq_ocorrencia_p;

			if (coalesce(nr_seq_regra_w,0) > 0) then
				ds_retorno_w := 'S';
			else
				select	max(nr_sequencia)
				into STRICT	nr_seq_regra_w
				from	pls_ocorrencia_conta_web
				where	nr_seq_ocorrencia = nr_seq_ocorrencia_p;
			end if;
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
