CREATE OR REPLACE FUNCTION pls_obter_se_operadora_online () RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se a operadora é on-line ou off-line no SCS.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [X] Tasy (Delphi/Java) [X] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Antes de realziar qualquer alterção, contactar o pessoal do Atendimento.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_situacao_w	varchar(2);
ds_retorno_w	varchar(2);


BEGIN

select	max(ie_situacao)
into STRICT	ie_situacao_w
from	pls_parametros_scs;

select	CASE WHEN ie_situacao_w='A' THEN 'S'  ELSE 'N' END
into STRICT	ds_retorno_w
;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
