CREATE OR REPLACE FUNCTION pls_obter_se_pagador_isento ( nr_seq_pagador_p bigint, cd_cgc_p text, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1)	:= 'N';
ie_isentar_ocorrencia_w		varchar(1);

c01 CURSOR FOR
	SELECT	coalesce(ie_isentar_ocorrencia,'S')
	from	pls_pag_isencao_bloq_fin a
	where	coalesce(dt_referencia_p,clock_timestamp()) between dt_inicio_vigencia and fim_dia(coalesce(dt_fim_vigencia,coalesce(dt_referencia_p,clock_timestamp())))
	and	nr_seq_pagador = nr_seq_pagador_p
	
union all

	SELECT	coalesce(ie_isentar_ocorrencia,'S')
	from	pls_pag_isencao_bloq_fin a
	where	coalesce(dt_referencia_p,clock_timestamp()) between dt_inicio_vigencia and fim_dia(coalesce(dt_fim_vigencia,coalesce(dt_referencia_p,clock_timestamp())))
	and	cd_cgc	= cd_cgc_p;


BEGIN
if (nr_seq_pagador_p IS NOT NULL AND nr_seq_pagador_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		ie_isentar_ocorrencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		null;
		end;
	end loop;
	close C01;

	if (ie_isentar_ocorrencia_w = 'S') then
		ie_retorno_w	:= 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
