CREATE OR REPLACE FUNCTION pls_obter_se_pagador_notif ( nr_seq_pagador_p bigint, nr_seq_lote_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1)	:= 'N';
qt_seg_lote_w			smallint	:= 0;


BEGIN

if (nr_seq_lote_p	= 0) then
	ds_retorno_w := 'S';
else
	select	count(*)
	into STRICT	qt_seg_lote_w
	from	pls_notificacao_pagador
	where	nr_seq_pagador	= nr_seq_pagador_p
	and	nr_seq_lote	= nr_seq_lote_p;

	if (qt_seg_lote_w > 0) then
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
