CREATE OR REPLACE FUNCTION pls_obter_se_pagador ( nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
cd_pessoa_fisica_w	varchar(10);
nr_seq_contrato_w	bigint;
qt_registro_w		bigint;


BEGIN

select	cd_pessoa_fisica,
	nr_seq_contrato
into STRICT	cd_pessoa_fisica_w,
	nr_seq_contrato_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

select	count(1)
into STRICT	qt_registro_w
from	pls_contrato_pagador
where	cd_pessoa_fisica		= cd_pessoa_fisica_w
and	nr_seq_contrato			= nr_seq_contrato_w
and (trunc(dt_rescisao,'dd')	> trunc(clock_timestamp(),'dd')
or	coalesce(dt_rescisao::text, '') = '');

if (qt_registro_w = 0) then
	ds_retorno_w := 'N';
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
