SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_parente_legal ( nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1)	:= 'N';
nr_seq_parentesco_w		bigint;
ie_tipo_parentesco_w		varchar(3);


BEGIN

select	coalesce(max(nr_seq_parentesco),0)
into STRICT	nr_seq_parentesco_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

if (nr_seq_parentesco_w	> 0) then
	select	coalesce(max(ie_tipo_parentesco),'X')
	into STRICT	ie_tipo_parentesco_w
	from	grau_parentesco
	where	nr_sequencia	= nr_seq_parentesco_w;

	if (ie_tipo_parentesco_w = '1') then
		ds_retorno_w	:= 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
