CREATE OR REPLACE FUNCTION pls_obter_se_partic_cancelado (nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


qt_partic_conta_w	integer;
qt_partic_cancelado_w	integer;
ie_partic_zero_w	varchar(1);

BEGIN
select	coalesce(sum(CASE WHEN ie_status='C' THEN 1  ELSE 0 END ),0),
	count(1)
into STRICT	qt_partic_cancelado_w,
	qt_partic_conta_w
from	pls_proc_participante
where	nr_seq_conta_proc = nr_seq_conta_proc_p;

/*Irá buscar se no procedimento existem participantes que não foram cancelados.*/

if (qt_partic_cancelado_w = qt_partic_conta_w) and (qt_partic_conta_w > 0) then
	ie_partic_zero_w := 'S';
else
	ie_partic_zero_w := 'N';
end if;
return	ie_partic_zero_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
