SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_pedido_insist ( nr_seq_requisicao_p pls_requisicao.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Obter se deverá habilitar a opção para realização de pedido de insistência no Portal Web.
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  x] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(1)	:= 'N';
ie_estagio_w		pls_requisicao.ie_estagio%type;
ie_tipo_processo_w	pls_requisicao.ie_tipo_processo%type;
ie_tipo_intercambio_w	pls_requisicao.ie_tipo_intercambio%type;
qt_transacao_w		integer;


BEGIN
select	ie_estagio,
	ie_tipo_processo,
	ie_tipo_intercambio
into STRICT	ie_estagio_w,
	ie_tipo_processo_w,
	ie_tipo_intercambio_w
from	pls_requisicao
where	nr_sequencia	= nr_seq_requisicao_p;

--Reprovada
if (ie_estagio_w	= 7) and (ie_tipo_processo_w	= 'I') and (ie_tipo_intercambio_w	= 'I') then
	select	count(1)
	into STRICT	qt_transacao_w
	from	ptu_pedido_insistencia
	where  	nr_seq_requisicao = nr_seq_requisicao_p;
	
	--Pedido de insistência
	if (qt_transacao_w = 0) then
		select	count(1)
		into STRICT	qt_transacao_w
		from	ptu_resposta_autorizacao
		where	nr_seq_requisicao = nr_seq_requisicao_p;

		--Resposta de autorização
		if (qt_transacao_w > 0) then
			select	count(1)
			into STRICT	qt_transacao_w
			from	ptu_resposta_auditoria
			where	nr_seq_requisicao = nr_seq_requisicao_p;
			
			--Resposta de auditoria
			if (qt_transacao_w < 2) then
				ds_retorno_w	:= 'S';
			end if;
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_pedido_insist ( nr_seq_requisicao_p pls_requisicao.nr_sequencia%type) FROM PUBLIC;

