SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_pessoa_lote_rev ( nr_seq_segurado_p bigint, nr_seq_pagador_p bigint, nr_seq_lote_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1)	:= 'N';
qt_seg_lote_w			smallint	:= 0;


BEGIN

if (coalesce(nr_seq_lote_p,0)	= 0) then
	ds_retorno_w := 'S';
else
	if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
		select	count(*)
		into STRICT	qt_seg_lote_w
		from	pls_pessoa_inconsistente b,
			pls_segurado a
		where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
		and	a.nr_sequencia		= nr_seq_segurado_p
		and	b.nr_seq_lote		= nr_seq_lote_p;

		if (qt_seg_lote_w > 0) then
			ds_retorno_w := 'S';
		end if;
	elsif (nr_seq_pagador_p IS NOT NULL AND nr_seq_pagador_p::text <> '') then
		select	sum(qt_registro)
		into STRICT	qt_seg_lote_w
		from (SELECT	count(*) qt_registro
			from	pls_pessoa_inconsistente b,
				pls_contrato_pagador a
			where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
			and	a.nr_sequencia		= nr_seq_pagador_p
			and	b.nr_seq_lote		= nr_seq_lote_p
			
union all

			SELECT	count(*) qt_registro
			from	pls_pessoa_inconsistente b,
				pls_contrato_pagador a
			where	a.cd_cgc		= b.cd_cgc
			and	a.nr_sequencia		= nr_seq_pagador_p
			and	b.nr_seq_lote		= nr_seq_lote_p) alias4;

		if (qt_seg_lote_w > 0) then
			ds_retorno_w := 'S';
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
