SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_pessoa_plano (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'N';
qt_registro_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	pls_contrato
where	cd_pf_estipulante	= cd_pessoa_fisica_p
and	((coalesce(dt_rescisao_contrato::text, '') = '')
or (dt_rescisao_contrato > clock_timestamp()));

if (qt_registro_w > 0) then
	ie_retorno_w	:= 'S';
else
	select	count(*)
	into STRICT	qt_registro_w
	from	pls_contrato_pagador
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	((coalesce(dt_rescisao::text, '') = '')
	or (dt_rescisao > clock_timestamp()));

	if (qt_registro_w > 0) then
		ie_retorno_w	:= 'S';
	else
		select	count(*)
		into STRICT	qt_registro_w
		from	pls_segurado
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	((coalesce(dt_rescisao::text, '') = '')
		or (dt_rescisao > clock_timestamp()));

		if (qt_registro_w > 0) then
			ie_retorno_w	:= 'S';
		else
			select	count(*)
			into STRICT	qt_registro_w
			from	pls_prestador
			where	cd_pessoa_fisica = cd_pessoa_fisica_p
			and	((coalesce(dt_exclusao::text, '') = '')
			or (dt_exclusao > clock_timestamp()));


			if (qt_registro_w > 0) then
				ie_retorno_w	:= 'S';
			end if;
		end if;
	end if;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
