CREATE OR REPLACE FUNCTION pls_obter_se_pf_pagadro_plano ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(10);
qt_registros_w		bigint;


BEGIN

ie_retorno_w	:= 'N';

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	count(*)
	into STRICT	qt_registros_w
	from	pls_contrato_pagador
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	((coalesce(dt_rescisao::text, '') = '')
	or (dt_rescisao > clock_timestamp()));

	if (qt_registros_w > 0) then
		ie_retorno_w	:= 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
