CREATE OR REPLACE FUNCTION pls_obter_se_pf_senha (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);
ds_senha_w	varchar(20);


BEGIN

select	max(ds_senha)
into STRICT	ds_senha_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

if (coalesce(ds_senha_w,'0') = '0') then
	ie_retorno_w	:= 'N';
else
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
